import type { Config } from "tailwindcss";

export default {
  darkMode: ["class"],
  content: ["./client/index.html", "./client/src/**/*.{js,jsx,ts,tsx}"],
  theme: {
    extend: {
      borderRadius: {
        lg: ".5625rem", /* 9px */
        md: ".375rem", /* 6px */
        sm: ".1875rem", /* 3px */
      },
      colors: {
        // Flat / base colors (regular buttons)
        background: "hsl(var(--background) / <alpha-value>)",
        foreground: "hsl(var(--foreground) / <alpha-value>)",
        border: "hsl(var(--border) / <alpha-value>)",
        input: "hsl(var(--input) / <alpha-value>)",
        card: {
          DEFAULT: "hsl(var(--card) / <alpha-value>)",
          foreground: "hsl(var(--card-foreground) / <alpha-value>)",
          border: "hsl(var(--card-border) / <alpha-value>)",
        },
        popover: {
          DEFAULT: "hsl(var(--popover) / <alpha-value>)",
          foreground: "hsl(var(--popover-foreground) / <alpha-value>)",
          border: "hsl(var(--popover-border) / <alpha-value>)",
        },
        primary: {
          DEFAULT: "hsl(var(--primary) / <alpha-value>)",
          foreground: "hsl(var(--primary-foreground) / <alpha-value>)",
          border: "var(--primary-border)",
        },
        secondary: {
          DEFAULT: "hsl(var(--secondary) / <alpha-value>)",
          foreground: "hsl(var(--secondary-foreground) / <alpha-value>)",
          border: "var(--secondary-border)",
        },
        muted: {
          DEFAULT: "hsl(var(--muted) / <alpha-value>)",
          foreground: "hsl(var(--muted-foreground) / <alpha-value>)",
          border: "var(--muted-border)",
        },
        accent: {
          DEFAULT: "hsl(var(--accent) / <alpha-value>)",
          foreground: "hsl(var(--accent-foreground) / <alpha-value>)",
          border: "var(--accent-border)",
        },
        destructive: {
          DEFAULT: "hsl(var(--destructive) / <alpha-value>)",
          foreground: "hsl(var(--destructive-foreground) / <alpha-value>)",
          border: "var(--destructive-border)",
        },
        ring: "hsl(var(--ring) / <alpha-value>)",
        chart: {
          "1": "hsl(var(--chart-1) / <alpha-value>)",
          "2": "hsl(var(--chart-2) / <alpha-value>)",
          "3": "hsl(var(--chart-3) / <alpha-value>)",
          "4": "hsl(var(--chart-4) / <alpha-value>)",
          "5": "hsl(var(--chart-5) / <alpha-value>)",
        },
        sidebar: {
          ring: "hsl(var(--sidebar-ring) / <alpha-value>)",
          DEFAULT: "hsl(var(--sidebar) / <alpha-value>)",
          foreground: "hsl(var(--sidebar-foreground) / <alpha-value>)",
          border: "hsl(var(--sidebar-border) / <alpha-value>)",
        },
        "sidebar-primary": {
          DEFAULT: "hsl(var(--sidebar-primary) / <alpha-value>)",
          foreground: "hsl(var(--sidebar-primary-foreground) / <alpha-value>)",
          border: "var(--sidebar-primary-border)",
        },
        "sidebar-accent": {
          DEFAULT: "hsl(var(--sidebar-accent) / <alpha-value>)",
          foreground: "hsl(var(--sidebar-accent-foreground) / <alpha-value>)",
          border: "var(--sidebar-accent-border)"
        },
        status: {
          online: "rgb(34 197 94)",
          away: "rgb(245 158 11)",
          busy: "rgb(239 68 68)",
          offline: "rgb(156 163 175)",
        },
        jira: {
          // Issue type colors
          bug: "rgb(232 67 147)",
          story: "rgb(34 197 94)", 
          task: "rgb(59 130 246)",
          epic: "rgb(147 51 234)",
          // Priority colors
          highest: "rgb(239 68 68)",
          high: "rgb(245 158 11)",
          medium: "rgb(34 197 94)",
          low: "rgb(34 197 94)",
          lowest: "rgb(156 163 175)",
          // Status colors
          todo: "rgb(107 114 126)",
          "in-progress": "rgb(0 101 255)",
          done: "rgb(0 135 90)",
          // Jira brand colors
          blue: "rgb(0 101 255)",
          "blue-light": "rgb(222 235 255)",
          "blue-dark": "rgb(0 82 204)",
          "gray-50": "rgb(250 251 252)",
          "gray-100": "rgb(244 245 247)",
          "gray-200": "rgb(223 225 229)",
          "gray-300": "rgb(193 199 208)",
          "gray-400": "rgb(151 160 175)",
          "gray-500": "rgb(107 114 126)",
          "gray-600": "rgb(68 84 111)",
          "gray-700": "rgb(52 69 99)",
          "gray-800": "rgb(37 56 88)",
          "gray-900": "rgb(22 43 77)",
        },
      },
      fontFamily: {
        sans: ["var(--font-sans)"],
        serif: ["var(--font-serif)"],
        mono: ["var(--font-mono)"],
      },
      keyframes: {
        "accordion-down": {
          from: { height: "0" },
          to: { height: "var(--radix-accordion-content-height)" },
        },
        "accordion-up": {
          from: { height: "var(--radix-accordion-content-height)" },
          to: { height: "0" },
        },
      },
      animation: {
        "accordion-down": "accordion-down 0.2s ease-out",
        "accordion-up": "accordion-up 0.2s ease-out",
      },
    },
  },
  plugins: [require("tailwindcss-animate"), require("@tailwindcss/typography")],
} satisfies Config;

vite.config.ts.content:
import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
          await import("@replit/vite-plugin-dev-banner").then((m) =>
            m.devBanner(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    host: "0.0.0.0",
    port: 5000,
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

shared/types.ts.content:
// Jira data types for frontend-only mock data
export interface JiraUser {
  id: string;
  name: string;
  email: string;
  avatarUrl?: string;
  initials: string;
}

export interface JiraProject {
  id: string;
  key: string;
  name: string;
  description: string;
  lead: JiraUser;
  category: string;
  type: 'software' | 'business';
  avatarUrl?: string;
  githubUrl?: string;
  ProjectUrl?: string;
}

export type IssueType = 'story' | 'bug' | 'task' | 'epic';
export type IssuePriority = 'highest' | 'high' | 'medium' | 'low' | 'lowest';
export type IssueStatus = 'to-do' | 'in-progress' | 'done';

export interface JiraIssue {
  id: string;
  key: string;
  summary: string;
  description: string;
  type: IssueType;
  priority: IssuePriority;
  status: IssueStatus;
  assignee?: JiraUser;
  reporter: JiraUser;
  project: JiraProject;
  storyPoints?: number;
  createdAt: string;
  updatedAt: string;
  labels: string[];
  comments: JiraComment[];
}

export interface JiraComment {
  id: string;
  author: JiraUser;
  body: string;
  createdAt: string;
  updatedAt: string;
}

export interface JiraSprint {
  id: string;
  name: string;
  state: 'future' | 'active' | 'closed';
  startDate?: string;
  endDate?: string;
  completeDate?: string;
  goal?: string;
  issues: JiraIssue[];
}

export interface KanbanColumn {
  id: string;
  title: string;
  status: IssueStatus;
  issues: JiraIssue[];
  limit?: number;
}

client/src/App.tsx.content:
import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { WorkflowProvider } from "@/contexts/WorkflowContext";
import StartupOverlay from "@/components/StartupOverlay";
import Board from "@/pages/Board";
import Dashboard from "@/pages/Dashboard";
import Backlog from "@/pages/Backlog";
import Code from "@/pages/Code";
import Releases from "@/pages/Releases";
import Roadmap from "@/pages/Roadmap";
import Notifications from "@/pages/Notifications";
import ProjectSettings from "@/pages/ProjectSettings";
import Help from "@/pages/Help";
import YourWork from "@/pages/YourWork";
import Projects from "@/pages/Projects";
import Filters from "@/pages/Filters";
import Teams from "@/pages/Teams";
import NotFound from "@/pages/not-found";

function Router() {
  return (
    <Switch>
      <Route path="/" component={Board} />
      <Route path="/board" component={Board} />
      <Route path="/dashboard" component={Dashboard} />
      <Route path="/backlog" component={Backlog} />
      <Route path="/code" component={Code} />
      <Route path="/releases" component={Releases} />
      <Route path="/roadmap" component={Roadmap} />
      <Route path="/notifications" component={Notifications} />
      <Route path="/settings" component={ProjectSettings} />
      <Route path="/help" component={Help} />
      <Route path="/your-work" component={YourWork} />
      <Route path="/projects" component={Projects} />
      <Route path="/filters" component={Filters} />
      <Route path="/teams" component={Teams} />
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <WorkflowProvider>
          <Toaster />
          <StartupOverlay />
          <Router />
        </WorkflowProvider>
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

client/src/index.css.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  --background: 0 0% 100%;

  --foreground: 216 25% 15%;

  --border: 0 0% 85%;

  --card: 0 0% 98%;

  --card-foreground: 216 25% 15%;

  --card-border: 0 0% 92%;

  --sidebar: 0 0% 97%;

  --sidebar-foreground: 216 25% 15%;

  --sidebar-border: 0 0% 90%;

  --sidebar-primary: 216 100% 50%;

  --sidebar-primary-foreground: 0 0% 100%;

  --sidebar-accent: 0 0% 94%;

  --sidebar-accent-foreground: 216 25% 15%;

  --sidebar-ring: 216 100% 50%;

  --popover: 0 0% 95%;

  --popover-foreground: 216 25% 15%;

  --popover-border: 0 0% 88%;

  --primary: 216 100% 50%;

  --primary-foreground: 0 0% 100%;

  --secondary: 0 0% 92%;

  --secondary-foreground: 216 25% 15%;

  --muted: 0 0% 94%;

  --muted-foreground: 0 0% 45%;

  --accent: 0 0% 93%;

  --accent-foreground: 216 25% 15%;

  --destructive: 0 70% 50%;

  --destructive-foreground: 0 0% 100%;

  --input: 0 0% 75%;
  --ring: 216 100% 50%;
  --chart-1: 216 100% 40%;
  --chart-2: 120 50% 40%;
  --chart-3: 35 85% 55%;
  --chart-4: 280 60% 50%;
  --chart-5: 200 80% 45%;

  --font-sans: Inter, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: "JetBrains Mono", Consolas, "Monaco", monospace;
  --radius: .5rem; /* 8px */
  --shadow-2xs: 0px 1px 2px 0px hsl(0 0% 0% / 0.05);
  --shadow-xs: 0px 1px 3px 0px hsl(0 0% 0% / 0.1), 0px 1px 2px 0px hsl(0 0% 0% / 0.06);
  --shadow-sm: 0px 1px 3px 0px hsl(0 0% 0% / 0.1), 0px 1px 2px -1px hsl(0 0% 0% / 0.1);
  --shadow: 0px 4px 6px -1px hsl(0 0% 0% / 0.1), 0px 2px 4px -2px hsl(0 0% 0% / 0.1);
  --shadow-md: 0px 6px 16px -4px hsl(0 0% 0% / 0.1), 0px 4px 6px -2px hsl(0 0% 0% / 0.05);
  --shadow-lg: 0px 10px 15px -3px hsl(0 0% 0% / 0.1), 0px 4px 6px -4px hsl(0 0% 0% / 0.1);
  --shadow-xl: 0px 20px 25px -5px hsl(0 0% 0% / 0.1), 0px 8px 10px -6px hsl(0 0% 0% / 0.1);
  --shadow-2xl: 0px 25px 50px -12px hsl(0 0% 0% / 0.25);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

.dark {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);

  --opaque-button-border-intensity: 9;  /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(255,255,255, .04);
  --elevate-2: rgba(255,255,255, .09);

  --background: 216 6% 8%;

  --foreground: 0 0% 95%;

  --border: 216 6% 15%;

  --card: 216 5% 10%;

  --card-foreground: 0 0% 95%;

  --card-border: 216 6% 16%;

  --sidebar: 216 6% 12%;

  --sidebar-foreground: 0 0% 95%;

  --sidebar-border: 216 6% 18%;

  --sidebar-primary: 216 100% 50%;

  --sidebar-primary-foreground: 0 0% 100%;

  --sidebar-accent: 216 5% 16%;

  --sidebar-accent-foreground: 0 0% 85%;

  --sidebar-ring: 216 100% 60%;

  --popover: 216 5% 14%;

  --popover-foreground: 0 0% 95%;

  --popover-border: 216 6% 20%;

  --primary: 216 100% 50%;

  --primary-foreground: 0 0% 100%;

  --secondary: 216 5% 18%;

  --secondary-foreground: 0 0% 85%;

  --muted: 216 5% 16%;

  --muted-foreground: 0 0% 60%;

  --accent: 216 5% 17%;

  --accent-foreground: 0 0% 85%;

  --destructive: 0 70% 50%;

  --destructive-foreground: 0 0% 100%;

  /* Used as the border around inputs. Dark mode: Should be a border that is light enough to have high contrast when rendered on a --card background. More contrast than standard --border */
  --input: 216 6% 25%;
  --ring: 216 100% 60%;
  --chart-1: 216 100% 65%;
  --chart-2: 120 50% 60%;
  --chart-3: 35 85% 65%;
  --chart-4: 280 60% 70%;
  --chart-5: 200 80% 65%;

  --shadow-2xs: 0px 1px 2px 0px hsl(0 0% 0% / 0.3);
  --shadow-xs: 0px 1px 3px 0px hsl(0 0% 0% / 0.4), 0px 1px 2px 0px hsl(0 0% 0% / 0.3);
  --shadow-sm: 0px 1px 3px 0px hsl(0 0% 0% / 0.4), 0px 1px 2px -1px hsl(0 0% 0% / 0.4);
  --shadow: 0px 4px 6px -1px hsl(0 0% 0% / 0.4), 0px 2px 4px -2px hsl(0 0% 0% / 0.4);
  --shadow-md: 0px 6px 16px -4px hsl(0 0% 0% / 0.4), 0px 4px 6px -2px hsl(0 0% 0% / 0.3);
  --shadow-lg: 0px 10px 15px -3px hsl(0 0% 0% / 0.4), 0px 4px 6px -4px hsl(0 0% 0% / 0.4);
  --shadow-xl: 0px 20px 25px -5px hsl(0 0% 0% / 0.4), 0px 8px 10px -6px hsl(0 0% 0% / 0.4);
  --shadow-2xl: 0px 25px 50px -12px hsl(0 0% 0% / 0.5);

}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }

  /* Smooth scrolling for better UX */
  html {
    scroll-behavior: smooth;
  }

  /* Better touch interactions on mobile */
  @media (hover: none) and (pointer: coarse) {
    button, a, [role="button"] {
      -webkit-tap-highlight-color: rgba(0, 0, 0, 0.1);
    }
  }
}

/**
 * Using the elevate system.
 * Automatic contrast adjustment.
 *
 * <element className="hover-elevate" />
 * <element className="active-elevate-2" />
 *
 * // Using the tailwind utility when a data attribute is "on"
 * <element className="toggle-elevate data-[state=on]:toggle-elevated" />
 * // Or manually controlling the toggle state
 * <element className="toggle-elevate toggle-elevated" />
 *
 * Elevation systems have to handle many states.
 * - not-hovered, vs. hovered vs. active  (three mutually exclusive states)
 * - toggled or not
 * - focused or not (this is not handled with these utilities)
 *
 * Even without handling focused or not, this is six possible combinations that
 * need to be distinguished from eachother visually.
 */
@layer utilities {

  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* .no-default-hover-elevate/no-default-active-elevate is an escape hatch so consumers of
   * buttons/badges can remove the automatic brightness adjustment on interactions
   * and program their own. */
  .no-default-hover-elevate {}

  .no-default-active-elevate {}


  /**
   * Toggleable backgrounds go behind the content. Hoverable/active goes on top.
   * This way they can stack/compound. Both will overlap the parent's borders!
   * So borders will be automatically adjusted both on toggle, and hover/active,
   * and they will be compounded.
   */
  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: -1;
    /* sits behind content but above backdrop */
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.toggle-elevate::before {
    inset: -1px;
  }

  /* Does not work on elements with overflow:hidden! */
  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: 999;
    /* sits in front of content */
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }
}

/* Shimmer animation for Code tab */
@keyframes shimmer {
  0% {
    background-position: -200% center;
  }
  100% {
    background-position: 200% center;
  }
}

@keyframes gradient-shift {
  0%, 100% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
}

.animate-shimmer {
  background: linear-gradient(
    90deg,
    transparent 0%,
    rgba(59, 130, 246, 0.8) 25%,
    rgba(147, 51, 234, 0.8) 50%,
    rgba(236, 72, 153, 0.8) 75%,
    transparent 100%
  );
  background-size: 200% 100%;
  animation: shimmer 3s ease-in-out infinite;
}

.gradient-text {
  background: linear-gradient(
    90deg,
    #3b82f6,
    #8b5cf6,
    #ec4899,
    #3b82f6
  );
  background-size: 200% auto;
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  animation: gradient-shift 3s ease infinite;
  font-weight: 600;
}

