  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import { Badge } from '@/components/ui/badge';
import { mockUsers, mockSprints } from '@/data/mockData';
import FilterDropdown, { FilterOptions } from './FilterDropdown';
import { JiraIssue } from '@shared/types';

interface BoardHeaderProps {
  boardName?: string;
  sprintName?: string;
  onSearch?: (query: string) => void;
  onFilterChange?: (filters: FilterOptions) => void;
  onCreateIssue?: () => void;
  filters?: FilterOptions;
  issues?: JiraIssue[];
}

export default function BoardHeader({ 
  boardName = 'Active sprints', 
  sprintName,
  onSearch,
  onFilterChange,
  onCreateIssue,
  filters,
  issues = []
}: BoardHeaderProps) {
  const [searchQuery, setSearchQuery] = useState('');
  const [isStarred, setIsStarred] = useState(false);

  const activeSprint = mockSprints.find(s => s.state === 'active'); // todo: remove mock functionality
  const assignedUsers = mockUsers.slice(0, 3); // todo: remove mock functionality

  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    setSearchQuery(value);
    onSearch?.(value);
  };

  const handleStarToggle = () => {
    setIsStarred(!isStarred);
    console.log(`Board ${isStarred ? 'unstarred' : 'starred'}`);
  };

  return (
    <div className="bg-white border-b border-jira-gray-200 px-3 sm:px-4 md:px-6 py-3 sm:py-4">
      {/* Top row - Board title and actions */}
      <div className="flex items-center justify-between mb-3 sm:mb-4">
        <div className="flex items-center space-x-2 sm:space-x-3 min-w-0 flex-1">
          <h1 className="text-xl sm:text-2xl font-semibold text-jira-gray-900 truncate" data-testid="text-board-title">
            {boardName}
          </h1>
          <Button
            variant="ghost"
            size="icon"
            onClick={handleStarToggle}
            className={`h-7 w-7 sm:h-8 sm:w-8 flex-shrink-0 transition-colors ${isStarred ? 'text-yellow-500' : 'text-jira-gray-400 hover:text-yellow-500'}`}
            data-testid="button-star-board"
          >
            <Star className={`h-4 w-4 ${isStarred ? 'fill-current' : ''}`} />
          </Button>
        </div>
      </div>

      {/* Sprint info */}
      {activeSprint && (
        <div className="mb-3 sm:mb-4">
          <div className="flex flex-col sm:flex-row sm:items-center space-y-2 sm:space-y-0 sm:space-x-4">
            <div className="flex items-center space-x-2 sm:space-x-3">
              <Badge variant="secondary" className="bg-green-100 text-green-800 border-green-200 text-xs" data-testid="badge-sprint-status">
                Active Sprint
              </Badge>
              <span className="text-sm font-medium text-jira-gray-900 truncate" data-testid="text-sprint-name">
                {activeSprint.name}
              </span>
            </div>
            <span className="text-xs sm:text-sm text-jira-gray-500" data-testid="text-sprint-dates">
              {activeSprint.startDate && activeSprint.endDate && (
                `${new Date(activeSprint.startDate).toLocaleDateString()} - ${new Date(activeSprint.endDate).toLocaleDateString()}`
              )}
            </span>
          </div>
          {activeSprint.goal && (
            <p className="text-xs sm:text-sm text-jira-gray-600 mt-2" data-testid="text-sprint-goal">
              <strong>Sprint goal:</strong> {activeSprint.goal}
            </p>
          )}
        </div>
      )}

      {/* Filter and search row */}
      <div className="flex flex-col gap-3">
        <div className="flex items-center gap-2 min-w-0">
          {/* Search */}
          <div className="relative flex-1 max-w-md">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-jira-gray-400 h-4 w-4 pointer-events-none" />
            <Input
              type="search"
              placeholder="Search this board"
              value={searchQuery}
              onChange={handleSearchChange}
              className="pl-10 pr-4 py-2 w-full h-9 sm:h-10 bg-white border-jira-gray-200 focus:border-jira-blue focus:ring-jira-blue text-sm transition-colors"
              data-testid="input-board-search"
            />
          </div>

          {/* Filters and Create Button */}
          <div className="flex items-center gap-2 flex-shrink-0">
            {filters && onFilterChange && (
              <FilterDropdown 
                filters={filters}
                onFiltersChange={onFilterChange}
                issues={issues}
              />
            )}
            {onCreateIssue && (
              <Button 
                onClick={onCreateIssue}
                size="sm"
                className="bg-jira-blue hover:bg-jira-blue-dark text-white h-9 sm:h-10 transition-colors"
                data-testid="button-create-issue"
              >
                <Plus className="h-4 w-4 sm:mr-1" />
                <span className="hidden sm:inline">Create</span>
              </Button>
            )}
          </div>
        </div>


        {/* Assigned team members */}
        <div className="flex items-center space-x-2 sm:space-x-3">
          <span className="text-xs sm:text-sm text-jira-gray-500 flex-shrink-0">Assignees:</span>
          <div className="flex -space-x-1 sm:-space-x-2">
            {assignedUsers.slice(0, 3).map((user, index) => (
              <Avatar 
                key={user.id} 
                className="h-7 w-7 sm:h-8 sm:w-8 border-2 border-white hover:z-10 relative cursor-pointer transition-transform hover:scale-110"
                data-testid={`avatar-assignee-${user.id}`}
              >
                <AvatarImage src={user.avatarUrl} alt={user.name} />
                <AvatarFallback className="bg-jira-blue text-white text-xs font-medium">
                  {user.initials}
                </AvatarFallback>
              </Avatar>
            ))}
            {assignedUsers.length > 3 && (
              <div className="h-7 w-7 sm:h-8 sm:w-8 rounded-full bg-jira-gray-200 border-2 border-white flex items-center justify-center">
                <span className="text-xs text-jira-gray-600">+{assignedUsers.length - 3}</span>
              </div>
            )}
            <Link href="/teams">
              <Button 
                variant="outline" 
                size="icon" 
                className="h-7 w-7 sm:h-8 sm:w-8 rounded-full border-2 border-white bg-jira-gray-100 hover:bg-jira-gray-200 transition-colors"
                data-testid="button-add-assignee"
              >
                <span className="text-xs text-jira-gray-600">+</span>
              </Button>
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/CreateIssueModal.t.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Bug, CheckSquare, Square, Zap } from "lucide-react";
import { mockUsers } from "@/data/mockData";

interface CreateIssueModalProps {
  open: boolean;
  onClose: () => void;
  onSubmit: (issue: {
    summary: string;
    description: string;
    type: string;
    priority: string;
    assignee: string;
    storyPoints?: number;
  }) => void;
}

const issueTypes = [
  { value: "story", label: "Story", icon: Square, color: "text-green-500" },
  { value: "bug", label: "Bug", icon: Bug, color: "text-red-500" },
  { value: "task", label: "Task", icon: CheckSquare, color: "text-blue-500" },
  { value: "epic", label: "Epic", icon: Zap, color: "text-purple-500" },
];

const priorities = [
  { value: "lowest", label: "Lowest" },
  { value: "low", label: "Low" },
  { value: "medium", label: "Medium" },
  { value: "high", label: "High" },
  { value: "highest", label: "Highest" },
];

// Use mockUsers for assignee options
const assignees = mockUsers.map(user => ({
  value: user.id,
  label: user.name
}));

export default function CreateIssueModal({ open, onClose, onSubmit }: CreateIssueModalProps) {
  const [formData, setFormData] = useState({
    summary: "",
    description: "",
    type: "",
    priority: "medium",
    assignee: "",
    storyPoints: "",
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit({
      ...formData,
      storyPoints: formData.storyPoints ? parseInt(formData.storyPoints) : undefined,
    });
    setFormData({
      summary: "",
      description: "",
      type: "",
      priority: "medium",
      assignee: "",
      storyPoints: "",
    });
    onClose();
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl max-h-[95vh] sm:max-h-[90vh] overflow-y-auto" data-testid="modal-create-issue">
        <DialogHeader>
          <DialogTitle className="text-lg sm:text-xl">Create Issue</DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4 sm:space-y-6">
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="issue-type" className="text-sm">Issue Type *</Label>
              <Select 
                value={formData.type} 
                onValueChange={(value) => setFormData(prev => ({ ...prev, type: value }))}
                required
              >
                <SelectTrigger data-testid="select-issue-type" className="h-10">
                  <SelectValue placeholder="Select issue type" />
                </SelectTrigger>
                <SelectContent>
                  {issueTypes.map(type => {
                    const IconComponent = type.icon;
                    return (
                      <SelectItem key={type.value} value={type.value}>
                        <div className="flex items-center gap-2">
                          <IconComponent className={`w-4 h-4 ${type.color}`} />
                          {type.label}
                        </div>
                      </SelectItem>
                    );
                  })}
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <Label htmlFor="priority" className="text-sm">Priority</Label>
              <Select 
                value={formData.priority} 
                onValueChange={(value) => setFormData(prev => ({ ...prev, priority: value }))}
              >
                <SelectTrigger data-testid="select-priority" className="h-10">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {priorities.map(priority => (
                    <SelectItem key={priority.value} value={priority.value}>
                      {priority.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>

          <div className="space-y-2">
            <Label htmlFor="summary">Summary *</Label>
            <Input
              id="summary"
              value={formData.summary}
              onChange={(e) => setFormData(prev => ({ ...prev, summary: e.target.value }))}
              placeholder="Enter a brief summary of the issue"
              required
              data-testid="input-summary"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="description">Description</Label>
            <Textarea
              id="description"
              value={formData.description}
              onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
              placeholder="Describe the issue in detail"
              className="min-h-32"
              data-testid="textarea-description"
            />
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="assignee">Assignee</Label>
              <Select 
                value={formData.assignee} 
                onValueChange={(value) => setFormData(prev => ({ ...prev, assignee: value }))}
              >
                <SelectTrigger data-testid="select-assignee">
                  <SelectValue placeholder="Unassigned" />
                </SelectTrigger>
                <SelectContent>
                  {assignees.map(assignee => (
                    <SelectItem key={assignee.value} value={assignee.value}>
                      {assignee.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <Label htmlFor="story-points">Story Points</Label>
              <Input
                id="story-points"
                type="number"
                value={formData.storyPoints}
                onChange={(e) => setFormData(prev => ({ ...prev, storyPoints: e.target.value }))}
                placeholder="0"
                min="0"
                max="100"
                data-testid="input-story-points"
              />
            </div>
          </div>

          <DialogFooter>
            <Button type="button" variant="outline" onClick={onClose} data-testid="button-cancel">
              Cancel
            </Button>
            <Button type="submit" data-testid="button-create-issue">
              Create Issue
            </Button>
          </DialogFooter>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/FilterDropdown.tsx.content:
import { useState, useEffect } from 'react';
import { Filter, X, Tag, AlertCircle, CheckCircle, Clock } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Checkbox } from '@/components/ui/checkbox';
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from '@/components/ui/popover';
import {
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
} from '@/components/ui/tabs';
import { ScrollArea } from '@/components/ui/scroll-area';
import { JiraIssue, IssueStatus } from '@shared/types';

export interface FilterOptions {
  statuses: IssueStatus[];
  labels: string[];
}

interface FilterDropdownProps {
  filters: FilterOptions;
  onFiltersChange: (filters: FilterOptions) => void;
  issues: JiraIssue[];
}


const STATUS_CONFIG = {
  'to-do': { icon: <Clock className="h-3 w-3 text-gray-500" />, label: 'To Do', color: 'text-gray-500' },
  'in-progress': { icon: <AlertCircle className="h-3 w-3 text-blue-600" />, label: 'In Progress', color: 'text-blue-600' },
  done: { icon: <CheckCircle className="h-3 w-3 text-green-600" />, label: 'Done', color: 'text-green-600' },
};

export default function FilterDropdown({ filters, onFiltersChange, issues }: FilterDropdownProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [activeTab, setActiveTab] = useState('status');

  // Extract unique values from issues for filter options
  const uniqueLabels = Array.from(new Set(issues.flatMap(issue => issue.labels || [])));

  // Count active filters
  const activeFilterCount = Object.values(filters).reduce((count, filterValues) => {
    if (Array.isArray(filterValues)) {
      return count + filterValues.length;
    }
    return count + (filterValues ? 1 : 0);
  }, 0);

  const updateFilters = (updates: Partial<FilterOptions>) => {
    onFiltersChange({ ...filters, ...updates });
  };

  const clearAllFilters = () => {
    onFiltersChange({
      statuses: [],
      labels: [],
    });
    setIsOpen(false);
  };


  const handleStatusToggle = (status: IssueStatus, checked: boolean) => {
    const newStatuses = checked
      ? [...filters.statuses, status]
      : filters.statuses.filter(s => s !== status);
    updateFilters({ statuses: newStatuses });
  };

  const handleLabelToggle = (label: string, checked: boolean) => {
    const newLabels = checked
      ? [...filters.labels, label]
      : filters.labels.filter(l => l !== label);
    updateFilters({ labels: newLabels });
  };


  return (
    <Popover open={isOpen} onOpenChange={setIsOpen}>
      <PopoverTrigger asChild>
        <Button 
          variant="outline" 
          size="sm"
          className="relative"
          data-testid="button-filter"
        >
          <Filter className="h-4 w-4 mr-2" />
          Filter
          {activeFilterCount > 0 && (
            <Badge 
              variant="destructive" 
              className="absolute -top-2 -right-2 h-5 w-5 p-0 flex items-center justify-center text-xs"
              data-testid="badge-active-filters"
            >
              {activeFilterCount}
            </Badge>
          )}
        </Button>
      </PopoverTrigger>

      <PopoverContent className="w-80 p-0" align="start" data-testid="popover-filter">
        <div className="p-4 border-b border-jira-gray-200">
          <div className="flex items-center justify-between">
            <h3 className="font-medium text-jira-gray-900">Filters</h3>
            {activeFilterCount > 0 && (
              <Button 
                variant="ghost" 
                size="sm" 
                onClick={clearAllFilters}
                className="text-jira-gray-500 hover:text-jira-gray-700"
                data-testid="button-clear-filters"
              >
                <X className="h-4 w-4 mr-1" />
                Clear all
              </Button>
            )}
          </div>
        </div>

        <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
          <TabsList className="grid w-full grid-cols-2 p-0 h-auto bg-transparent">
            <TabsTrigger 
              value="status" 
              className="text-xs py-2 data-[state=active]:bg-jira-blue data-[state=active]:text-white"
              data-testid="tab-status"
            >
              <CheckCircle className="h-3 w-3 mr-1" />
              Status
            </TabsTrigger>
            <TabsTrigger 
              value="skills" 
              className="text-xs py-2 data-[state=active]:bg-jira-blue data-[state=active]:text-white"
              data-testid="tab-skills"
            >
              <Tag className="h-3 w-3 mr-1" />
              Skills
            </TabsTrigger>
          </TabsList>

          <ScrollArea className="h-80">
            <TabsContent value="status" className="m-0 p-4">
              <div className="space-y-3">
                {Object.entries(STATUS_CONFIG).map(([status, config]) => (
                  <div key={status} className="flex items-center space-x-2">
                    <Checkbox
                      id={`status-${status}`}
                      checked={filters.statuses.includes(status as IssueStatus)}
                      onCheckedChange={(checked) => handleStatusToggle(status as IssueStatus, !!checked)}
                      data-testid={`checkbox-status-${status}`}
                    />
                    <label htmlFor={`status-${status}`} className="text-sm text-jira-gray-700 cursor-pointer">
                      <div className="flex items-center space-x-2">
                        {config.icon}
                        <span>{config.label}</span>
                      </div>
                    </label>
                  </div>
                ))}
              </div>
            </TabsContent>

            <TabsContent value="skills" className="m-0 p-4">
              <div className="space-y-3">
                {uniqueLabels.map(label => (
                  <div key={label} className="flex items-center space-x-2">
                    <Checkbox
                      id={`label-${label}`}
                      checked={filters.labels.includes(label)}
                      onCheckedChange={(checked) => handleLabelToggle(label, !!checked)}
                      data-testid={`checkbox-label-${label}`}
                    />
                    <label htmlFor={`label-${label}`} className="text-sm text-jira-gray-700 cursor-pointer">
                      <Badge variant="secondary" className="text-xs">
                        {label}
                      </Badge>
                    </label>
                  </div>
                ))}
              </div>
            </TabsContent>
          </ScrollArea>
        </Tabs>
      </PopoverContent>
    </Popover>
  );
}

client/src/components/IssueCard.tsx.content:
import { ArrowUp, ArrowDown, Minus, Bug, CheckSquare, BookOpen, Zap, GripVertical } from 'lucide-react';
import { Card } from '@/components/ui/card';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Badge } from '@/components/ui/badge';
import { JiraIssue, IssuePriority, IssueType } from '@shared/types';

interface IssueCardProps {
  issue: JiraIssue;
  onClick?: (issue: JiraIssue) => void;
  isDragging?: boolean;
}

const priorityIcons: Record<IssuePriority, { icon: React.ComponentType<any>; color: string; }> = {
  highest: { icon: ArrowUp, color: 'text-red-500' },
  high: { icon: ArrowUp, color: 'text-orange-500' }, 
  medium: { icon: Minus, color: 'text-green-500' },
  low: { icon: ArrowDown, color: 'text-green-500' },
  lowest: { icon: ArrowDown, color: 'text-gray-400' }
};

const typeIcons: Record<IssueType, { icon: React.ComponentType<any>; color: string; }> = {
  story: { icon: BookOpen, color: 'text-green-500' },
  bug: { icon: Bug, color: 'text-red-500' },
  task: { icon: CheckSquare, color: 'text-blue-500' },
  epic: { icon: Zap, color: 'text-purple-500' }
};

export default function IssueCard({ issue, onClick, isDragging = false }: IssueCardProps) {
  const PriorityIcon = priorityIcons[issue.priority].icon;
  const TypeIcon = typeIcons[issue.type].icon;

  const handleClick = (e: React.MouseEvent) => {
    // Prevent click event during drag
    if (isDragging) {
      e.preventDefault();
      e.stopPropagation();
      return;
    }
    console.log('Issue card clicked:', issue.key);
    onClick?.(issue);
  };

  return (
    <Card 
      onClick={handleClick}
      className={`group p-3 sm:p-3.5 mb-2 sm:mb-3 bg-white border border-jira-gray-200 hover:bg-jira-gray-50 active:bg-jira-gray-100 transition-all duration-150 cursor-pointer touch-manipulation ${
        isDragging ? 'opacity-50 cursor-grabbing' : 'shadow-sm hover:shadow-md'
      }`}
      data-testid={`card-issue-${issue.key}`}
    >
      <div>
        {/* Issue Summary */}
        <h3 className="text-sm sm:text-sm font-medium text-jira-gray-900 mb-2 sm:mb-2.5 leading-snug line-clamp-2" data-testid={`text-issue-summary-${issue.id}`}>
          {issue.summary}
        </h3>

        {/* Labels */}
        {issue.labels.length > 0 && (
          <div className="flex flex-wrap gap-1.5 mb-2.5 sm:mb-3">
            {issue.labels.slice(0, 2).map((label) => (
              <Badge 
                key={label} 
                variant="secondary" 
                className="text-xs px-2 py-0.5 bg-jira-gray-100 text-jira-gray-700 hover:bg-jira-gray-200 transition-colors"
                data-testid={`badge-label-${label}`}
              >
                {label}
              </Badge>
            ))}
            {issue.labels.length > 2 && (
              <Badge 
                variant="secondary" 
                className="text-xs px-2 py-0.5 bg-jira-gray-100 text-jira-gray-700"
              >
                +{issue.labels.length - 2}
              </Badge>
            )}
          </div>
        )}

        {/* Bottom Row */}
        <div className="flex items-center justify-between mt-2.5 sm:mt-3">
          {/* Left side - Issue key, type, priority */}
          <div className="flex items-center space-x-1.5 sm:space-x-2">
            <span className="text-xs font-mono text-jira-gray-500 hover:text-jira-blue cursor-pointer transition-colors" data-testid={`text-issue-key-${issue.id}`}>
              {issue.key}
            </span>

            <div className="flex items-center space-x-1">
              <TypeIcon className={`h-3.5 w-3.5 sm:h-4 sm:w-4 ${typeIcons[issue.type].color}`} />
              <PriorityIcon className={`h-3.5 w-3.5 sm:h-4 sm:w-4 ${priorityIcons[issue.priority].color}`} />
            </div>

            {/* Story Points */}
            {issue.storyPoints && (
              <div className="w-5 h-5 sm:w-6 sm:h-6 rounded-full bg-jira-gray-100 flex items-center justify-center">
                <span className="text-xs font-semibold text-jira-gray-600" data-testid={`text-story-points-${issue.id}`}>
                  {issue.storyPoints}
                </span>
              </div>
            )}
          </div>

          {/* Right side - Assignee */}
          <div className="flex items-center">
            {issue.assignee ? (
              <Avatar className="h-6 w-6 sm:h-7 sm:w-7 ring-2 ring-white" data-testid={`avatar-assignee-${issue.id}`}>
                <AvatarImage src={issue.assignee.avatarUrl} alt={issue.assignee.name} />
                <AvatarFallback className="bg-jira-blue text-white text-xs font-medium">
                  {issue.assignee.initials}
                </AvatarFallback>
              </Avatar>
            ) : (
              <div className="w-6 h-6 sm:w-7 sm:h-7 rounded-full bg-jira-gray-200 flex items-center justify-center ring-2 ring-white" data-testid={`avatar-unassigned-${issue.id}`}>
                <span className="text-xs text-jira-gray-500">?</span>
              </div>
            )}
          </div>
        </div>
      </div>
    </Card>
  );
}

client/src/components/IssueModal.tsx.content:
import { useState } from 'react';
import { X, ArrowUp, ArrowDown, Minus, Bug, CheckSquare, BookOpen, Zap, Paperclip, MessageSquare, User, Calendar, Tag, MoreHorizontal, Plus } from 'lucide-react';
import { mockUsers } from '@/data/mockData';
import {
  Dialog,
  DialogContent,
  DialogHeader,
} from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Badge } from '@/components/ui/badge';
import { Textarea } from '@/components/ui/textarea';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { JiraIssue, IssuePriority, IssueType, IssueStatus } from '@shared/types';
import { formatDistanceToNow } from 'date-fns';

interface IssueModalProps {
  issue: JiraIssue | null;
  isOpen: boolean;
  onClose: () => void;
  onUpdate?: (updatedIssue: Partial<JiraIssue>) => void;
}

const priorityIcons: Record<IssuePriority, { icon: React.ComponentType<any>; color: string; label: string; }> = {
  highest: { icon: ArrowUp, color: 'text-red-500', label: 'Highest' },
  high: { icon: ArrowUp, color: 'text-orange-500', label: 'High' }, 
  medium: { icon: Minus, color: 'text-green-500', label: 'Medium' },
  low: { icon: ArrowDown, color: 'text-green-500', label: 'Low' },
  lowest: { icon: ArrowDown, color: 'text-gray-400', label: 'Lowest' }
};

const typeIcons: Record<IssueType, { icon: React.ComponentType<any>; color: string; label: string; }> = {
  story: { icon: BookOpen, color: 'text-green-500', label: 'Story' },
  bug: { icon: Bug, color: 'text-red-500', label: 'Bug' },
  task: { icon: CheckSquare, color: 'text-blue-500', label: 'Task' },
  epic: { icon: Zap, color: 'text-purple-500', label: 'Epic' }
};

const statusOptions: Record<IssueStatus, string> = {
  'to-do': 'To Do',
  'in-progress': 'In Progress',
  'done': 'Done'
};

export default function IssueModal({ issue, isOpen, onClose, onUpdate }: IssueModalProps) {
  const [isEditing, setIsEditing] = useState(false);
  const [newComment, setNewComment] = useState('');

  if (!issue) return null;

  const PriorityIcon = priorityIcons[issue.priority].icon;
  const TypeIcon = typeIcons[issue.type].icon;

  const handleEditToggle = () => {
    setIsEditing(!isEditing);
  };

  const handleSave = () => {
    console.log('Saving changes to issue:', issue.key);
    onUpdate?.({});
    setIsEditing(false);
  };

  const handleAddComment = () => {
    if (newComment.trim()) {
      console.log('Adding comment:', newComment);

      const newCommentObj = {
        id: Date.now().toString(),
        author: {
          id: 'current-user',
          name: 'You',
          email: 'you@company.com',
          initials: 'YU'
        },
        body: newComment.trim(),
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString()
      };

      onUpdate?.({ 
        comments: [...issue.comments, newCommentObj] 
      });

      setNewComment('');
    }
  };

  const handleFieldChange = (field: string, value: any) => {
    console.log(`Updating ${field}:`, value);
    onUpdate?.({ [field]: value });
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[95vh] sm:max-h-[90vh] overflow-hidden p-0 flex flex-col gap-0" data-testid="modal-issue">
        {/* Header */}
        <DialogHeader className="px-3 sm:px-6 py-3 sm:py-4 border-b border-jira-gray-200 flex flex-row items-center justify-between flex-shrink-0">
          <div className="flex items-center space-x-2 sm:space-x-3 min-w-0">
            <div className="flex items-center space-x-1 sm:space-x-2">
              <TypeIcon className={`h-4 w-4 ${typeIcons[issue.type].color}`} />
              <span className="text-xs sm:text-sm text-jira-gray-500">{typeIcons[issue.type].label}</span>
            </div>
            <span className="text-xs sm:text-sm font-mono text-jira-gray-500 truncate" data-testid={`text-modal-issue-key-${issue.id}`}>
              {issue.key}
            </span>
          </div>

        </DialogHeader>

        <div className="flex flex-col md:flex-row flex-1 overflow-hidden">
          {/* Main Content */}
          <div className="flex-1 p-3 sm:p-6 space-y-4 sm:space-y-6 overflow-y-auto">
            {/* Summary */}
            <div>
              <h1 className="text-lg sm:text-xl font-medium text-jira-gray-900 leading-tight" 
                  data-testid={`text-modal-issue-summary-${issue.id}`}>
                {issue.summary}
              </h1>
            </div>

            {/* Description */}
            <div>
              <h3 className="text-sm font-medium text-jira-gray-900 mb-2">Description</h3>
              <div 
                className="text-sm text-jira-gray-700 p-3 bg-jira-gray-50 rounded min-h-20"
                data-testid={`text-modal-issue-description-${issue.id}`}
              >
                {issue.description || 'No description provided'}
              </div>
            </div>

            {/* Edit Actions */}
            {isEditing && (
              <div className="flex space-x-2">
                <Button onClick={handleSave} size="sm" data-testid="button-save-changes">
                  Save
                </Button>
                <Button variant="outline" onClick={() => setIsEditing(false)} size="sm" data-testid="button-cancel-changes">
                  Cancel
                </Button>
              </div>
            )}

            {/* Comments */}
            <div>
              <h3 className="text-sm font-medium text-jira-gray-900 mb-3 sm:mb-4 flex items-center">
                <MessageSquare className="h-4 w-4 mr-2" />
                Comments ({issue.comments.length})
              </h3>

              {/* Add Comment */}
              <div className="mb-4">
                <div className="flex space-x-2 sm:space-x-3">
                  <Avatar className="h-7 w-7 sm:h-8 sm:w-8 flex-shrink-0">
                    <AvatarFallback className="bg-jira-blue text-white text-xs font-medium">
                      You
                    </AvatarFallback>
                  </Avatar>
                  <div className="flex-1 min-w-0">
                    <Textarea
                      value={newComment}
                      onChange={(e) => setNewComment(e.target.value)}
                      placeholder="Add a comment..."
                      rows={3}
                      className="resize-none text-sm"
                      data-testid="textarea-add-comment"
                    />
                    <div className="flex justify-end mt-2">
                      <Button 
                        onClick={handleAddComment}
                        size="sm"
                        disabled={!newComment.trim()}
                        data-testid="button-add-comment"
                        className="transition-opacity"
                      >
                        Comment
                      </Button>
                    </div>
                  </div>
                </div>
              </div>

              {/* Existing Comments */}
              <div className="space-y-3 sm:space-y-4">
                {issue.comments.map((comment) => (
                  <div key={comment.id} className="flex space-x-2 sm:space-x-3" data-testid={`comment-${comment.id}`}>
                    <Avatar className="h-7 w-7 sm:h-8 sm:w-8 flex-shrink-0">
                      <AvatarImage src={comment.author.avatarUrl} alt={comment.author.name} />
                      <AvatarFallback className="bg-jira-blue text-white text-xs font-medium">
                        {comment.author.initials}
                      </AvatarFallback>
                    </Avatar>
                    <div className="flex-1 min-w-0">
                      <div className="bg-jira-gray-50 p-2.5 sm:p-3 rounded border">
                        <div className="flex items-center justify-between mb-1 gap-2">
                          <span className="text-xs sm:text-sm font-medium text-jira-gray-900 truncate" data-testid={`text-comment-author-${comment.id}`}>
                            {comment.author.name}
                          </span>
                          <span className="text-xs text-jira-gray-500 flex-shrink-0" data-testid={`text-comment-date-${comment.id}`}>
                            {formatDistanceToNow(new Date(comment.createdAt), { addSuffix: true })}
                          </span>
                        </div>
                        <p className="text-xs sm:text-sm text-jira-gray-700" data-testid={`text-comment-body-${comment.id}`}>
                          {comment.body}
                        </p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* Sidebar */}
          <div className="w-full md:w-64 bg-jira-gray-50 border-t md:border-t-0 md:border-l border-jira-gray-200 p-3 sm:p-4 space-y-3 sm:space-y-4 flex-shrink-0 md:overflow-y-auto">
            <div>
              <label className="text-xs font-medium text-jira-gray-500 uppercase tracking-wide mb-2 block">
                Status
              </label>
              <Select 
                value={issue.status} 
                onValueChange={(value) => handleFieldChange('status', value)}
              >
                <SelectTrigger className="w-full" data-testid="select-status">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {Object.entries(statusOptions).map(([key, label]) => (
                    <SelectItem key={key} value={key}>
                      {label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <div className="flex items-center justify-between mb-2">
                <label className="text-xs font-medium text-jira-gray-500 uppercase tracking-wide">
                  Assignee
                </label>
                <Button
                  variant="ghost"
                  size="sm"
                  className="h-6 w-6 p-0 text-jira-gray-500 hover:text-jira-blue"
                  onClick={() => window.open('/project-settings', '_blank')}
                  title="Manage team members"
                >
                  <Plus className="h-3 w-3" />
                </Button>
              </div>
              <Select 
                value={issue.assignee?.id || 'unassigned'} 
                onValueChange={(value) => {
                  if (value === 'unassigned') {
                    handleFieldChange('assignee', undefined);
                  } else {
                    const user = mockUsers.find(u => u.id === value);
                    handleFieldChange('assignee', user);
                  }
                }}
              >
                <SelectTrigger className="w-full" data-testid="select-assignee">
                  <SelectValue>
                    <div className="flex items-center space-x-2">
                      {issue.assignee ? (
                        <>
                          <Avatar className="h-5 w-5">
                            <AvatarImage src={issue.assignee.avatarUrl} alt={issue.assignee.name} />
                            <AvatarFallback className="bg-jira-blue text-white text-xs">
                              {issue.assignee.initials}
                            </AvatarFallback>
                          </Avatar>
                          <span className="text-sm">{issue.assignee.name}</span>
                        </>
                      ) : (
                        <>
                          <User className="h-4 w-4 text-jira-gray-500" />
                          <span className="text-sm text-jira-gray-500">Unassigned</span>
                        </>
                      )}
                    </div>
                  </SelectValue>
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="unassigned">
                    <div className="flex items-center space-x-2">
                      <User className="h-4 w-4 text-jira-gray-500" />
                      <span>Unassigned</span>
                    </div>
                  </SelectItem>
                  {mockUsers.map((user) => (
                    <SelectItem key={user.id} value={user.id}>
                      <div className="flex items-center space-x-2">
                        <Avatar className="h-5 w-5">
                          <AvatarImage src={user.avatarUrl} alt={user.name} />
                          <AvatarFallback className="bg-jira-blue text-white text-xs">
                            {user.initials}
                          </AvatarFallback>
                        </Avatar>
                        <span>{user.name}</span>
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <label className="text-xs font-medium text-jira-gray-500 uppercase tracking-wide mb-2 block">
                Priority
              </label>
              <div className="flex items-center space-x-2 p-2 rounded border border-jira-gray-200 bg-white" data-testid="field-priority">
                <PriorityIcon className={`h-4 w-4 ${priorityIcons[issue.priority].color}`} />
                <span className="text-sm text-jira-gray-900">{priorityIcons[issue.priority].label}</span>
              </div>
            </div>

            <div>
              <label className="text-xs font-medium text-jira-gray-500 uppercase tracking-wide mb-2 block">
                Story Points
              </label>
              <div className="p-2 rounded border border-jira-gray-200 bg-white" data-testid="field-story-points">
                <span className="text-sm text-jira-gray-900">
                  {issue.storyPoints || 'None'}
                </span>
              </div>
            </div>

            <div>
              <label className="text-xs font-medium text-jira-gray-500 uppercase tracking-wide mb-2 block">
                Labels
              </label>
              <div className="flex flex-wrap gap-1">
                {issue.labels.map((label) => (
                  <Badge 
                    key={label} 
                    variant="secondary" 
                    className="text-xs px-2 py-0.5 bg-jira-gray-200 text-jira-gray-700"
                    data-testid={`badge-modal-label-${label}`}
                  >
                    {label}
                  </Badge>
                ))}

              </div>
            </div>

            <div className="pt-4 border-t border-jira-gray-200">
              <div className="text-xs text-jira-gray-500 space-y-1">
                <div data-testid="text-modal-created">
                  Created {formatDistanceToNow(new Date(issue.createdAt), { addSuffix: true })}
                </div>
                <div data-testid="text-modal-updated">
                  Updated {formatDistanceToNow(new Date(issue.updatedAt), { addSuffix: true })}
                </div>
                <div data-testid="text-modal-reporter">
                  Reporter: {issue.reporter.name}
                </div>
              </div>
            </div>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/JiraSidebar.tsx.content:
import { useState } from 'react';
import { useLocation, Link } from 'wouter';
import { ChevronDown, ChevronRight, Settings, FolderOpen, Users, Zap, Search, Star, Clock, UserCheck, Github, BarChart3 } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { mockProjects } from '@/data/mockData';

interface SidebarItemProps {
  icon: React.ReactNode;
  label: string | React.ReactNode;
  isActive?: boolean;
  onClick?: () => void;
  href?: string;
  hasSubmenu?: boolean;
  isExpanded?: boolean;
  onToggle?: () => void;
  children?: React.ReactNode;
  testId?: string;
}

function SidebarItem({ 
  icon, 
  label, 
  isActive = false, 
  onClick, 
  href,
  hasSubmenu = false, 
  isExpanded = false, 
  onToggle, 
  children,
  testId 
}: SidebarItemProps) {
  const content = (
    <Button
      variant="ghost"
      onClick={hasSubmenu ? onToggle : onClick}
      className={`w-full justify-start px-3 py-2 h-9 sm:h-10 text-sm font-normal text-jira-gray-700 hover:bg-jira-gray-100 transition-all duration-150 ${
        isActive ? 'bg-jira-blue-light text-jira-blue border-r-2 border-jira-blue font-medium' : ''
      }`}
      data-testid={testId}
      asChild={!!href && !hasSubmenu}
    >
      {href && !hasSubmenu ? (
        <Link href={href}>
          <div className="flex items-center justify-between w-full">
            <div className="flex items-center space-x-3">
              {icon}
              <span className="text-left">{label}</span>
            </div>
          </div>
        </Link>
      ) : (
        <div className="flex items-center justify-between w-full">
          <div className="flex items-center space-x-3">
            {icon}
            <span className="text-left">{label}</span>
          </div>
          {hasSubmenu && (
            <div className="ml-auto">
              {isExpanded ? (
                <ChevronDown className="h-3 w-3 text-jira-gray-500" />
              ) : (
                <ChevronRight className="h-3 w-3 text-jira-gray-500" />
              )}
            </div>
          )}
        </div>
      )}
    </Button>
  );

  return (
    <div className="w-full">
      {content}
      {hasSubmenu && isExpanded && (
        <div className="ml-6 mt-1 space-y-1">
          {children}
        </div>
      )}
    </div>
  );
}

interface JiraSidebarProps {
  isMobileMenuOpen?: boolean;
  onMobileMenuClose?: () => void;
}

export default function JiraSidebar({ isMobileMenuOpen = false, onMobileMenuClose }: JiraSidebarProps) {
  const [location] = useLocation();
  const [expandedItems, setExpandedItems] = useState<Record<string, boolean>>({
    projects: true,
    filters: false
  });

  const currentProject = mockProjects[0]; // todo: remove mock functionality

  const toggleExpanded = (key: string) => {
    setExpandedItems(prev => ({
      ...prev,
      [key]: !prev[key]
    }));
  };

  const isActive = (path: string) => {
    if (path === '/' || path === '/board') {
      return location === '/' || location === '/board';
    }
    return location === path;
  };

  return (
    <>
      {/* Mobile overlay */}
      {isMobileMenuOpen && (
        <div 
          className="fixed inset-0 bg-black/50 z-40 lg:hidden animate-in fade-in duration-200"
          onClick={onMobileMenuClose}
        />
      )}

      <aside className={`fixed lg:relative w-64 sm:w-72 lg:w-64 bg-white border-r border-jira-gray-200 h-screen overflow-y-auto transition-transform duration-300 ease-in-out z-50 ${isMobileMenuOpen ? 'translate-x-0' : '-translate-x-full'} lg:translate-x-0`}>
      {/* Project Header */}
      <div className="p-3 sm:p-4 border-b border-jira-gray-200 bg-jira-gray-50">
        <div className="flex items-center space-x-3">
          <Avatar className="h-9 w-9 sm:h-10 sm:w-10">
            <AvatarImage src={currentProject.avatarUrl || '/assets/jira-logo.jpg'} alt={currentProject.name} />
            <AvatarFallback className="bg-jira-blue text-white text-sm font-medium">
              {currentProject.key.substring(0, 2)}
            </AvatarFallback>
          </Avatar>
          <div className="flex-1 min-w-0">
            <h2 className="text-sm font-semibold text-jira-gray-900 truncate" data-testid="text-project-name">
              {currentProject.name}
            </h2>
            <p className="text-xs text-jira-gray-500" data-testid="text-project-type">
              Software project
            </p>
          </div>
        </div>
      </div>

      {/* Navigation Menu */}
      <nav className="p-2 sm:p-3 space-y-1">
        {/* Planning */}
        <div className="mb-4">
          <div className="px-2 py-2 mb-1">
            <span className="text-xs font-semibold text-jira-gray-500 uppercase tracking-wider">
              Planning
            </span>
          </div>
          <SidebarItem
            icon={<BarChart3 className="h-4 w-4" />}
            label="Roadmap"
            href="/roadmap"
            isActive={isActive('/roadmap')}
            testId="nav-roadmap"
          />
          <SidebarItem
            icon={<FolderOpen className="h-4 w-4" />}
            label="Backlog"
            href="/backlog"
            isActive={isActive('/backlog')}
            testId="nav-backlog"
          />
          <SidebarItem
            icon={<Zap className="h-4 w-4" />}
            label="Active sprints"
            href="/board"
            isActive={isActive('/board')}
            testId="nav-active-sprints"
          />
        </div>

        {/* Development */}
        <div className="mb-4">
          <div className="px-2 py-2 mb-1">
            <span className="text-xs font-semibold text-jira-gray-500 uppercase tracking-wider">
              Development
            </span>
          </div>
          <div className="relative">
            <SidebarItem
              icon={<Github className="h-4 w-4" />}
              label=""
              href="/code"
              isActive={isActive('/code')}
              testId="nav-code"
            />
            <div className="absolute inset-0 pointer-events-none flex items-center pl-11">
              <span className="gradient-text text-sm">Code</span>
            </div>
          </div>
           <div className="relative">
          <SidebarItem
            icon={<FolderOpen className="h-4 w-4" />}
            label=""
            href="/projects"
            isActive={isActive('/projects')}
            testId="nav-all-projects"

          />
           <div className="absolute inset-0 pointer-events-none flex items-center pl-11">
              <span className="gradient-text text-sm">Projects</span>
            </div>
</div>
          <SidebarItem
            icon={<UserCheck className="h-4 w-4" />}
            label="Releases"
            href="/releases"
            isActive={isActive('/releases')}
            testId="nav-releases"
          />
        </div>



        {/* Project Settings */}
        <div className="pt-4 mt-4 border-t border-jira-gray-200">
          <SidebarItem
            icon={<Settings className="h-4 w-4" />}
            label="Project settings"
            href="/settings"
            isActive={isActive('/settings')}
            testId="nav-project-settings"
          />
        </div>
      </nav>
      </aside>
    </>
  );
}

