import JiraHeader from '@/components/JiraHeader';
import JiraSidebar from '@/components/JiraSidebar';
import { Settings, Users, Tag, Flag, Workflow, Archive, Trash2, Plus, X, Edit, AlertTriangle } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Badge } from '@/components/ui/badge';
import { Switch } from '@/components/ui/switch';
import { Separator } from '@/components/ui/separator';
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { mockProjects, mockUsers } from '@/data/mockData';
import { useState } from 'react';
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle } from '@/components/ui/alert-dialog';
import { useWorkflow } from '@/contexts/WorkflowContext';

export default function ProjectSettings() {
  const project = mockProjects[0];
  const [projectName, setProjectName] = useState(project.name);
  const [projectDescription, setProjectDescription] = useState(project.description);
  const [projectKey, setProjectKey] = useState(project.key);

  // Team management state
  const [teamMembers, setTeamMembers] = useState(mockUsers);
  const [showAddMember, setShowAddMember] = useState(false);
  const [selectedUser, setSelectedUser] = useState('');

  // Labels management state
  const [labels, setLabels] = useState(['backend', 'frontend', 'design', 'ux', 'database', 'security', 'api', 'performance']);
  const [showCreateLabel, setShowCreateLabel] = useState(false);
  const [showEditLabel, setShowEditLabel] = useState(false);
  const [newLabelName, setNewLabelName] = useState('');
  const [editingLabel, setEditingLabel] = useState('');
  const [editedLabelName, setEditedLabelName] = useState('');

  // Workflow management state - using context
  const { columns, addColumn } = useWorkflow();
  const [showAddColumn, setShowAddColumn] = useState(false);
  const [newColumnTitle, setNewColumnTitle] = useState('');
  const [newColumnStatus, setNewColumnStatus] = useState('');
  const [newColumnLimit, setNewColumnLimit] = useState('');

  // Delete warning state
  const [showDeleteWarning, setShowDeleteWarning] = useState(false);

  const handleAddMember = () => {
    if (selectedUser) {
      const user = mockUsers.find(u => u.id === selectedUser);
      if (user && !teamMembers.find(m => m.id === user.id)) {
        setTeamMembers([...teamMembers, user]);
      }
      setShowAddMember(false);
      setSelectedUser('');
    }
  };

  const handleRemoveMember = (userId: string) => {
    setTeamMembers(teamMembers.filter(m => m.id !== userId));
  };

  const handleCreateLabel = () => {
    if (newLabelName.trim() && !labels.includes(newLabelName.trim())) {
      setLabels([...labels, newLabelName.trim()]);
      setNewLabelName('');
      setShowCreateLabel(false);
    }
  };

  const handleEditLabel = () => {
    if (editedLabelName.trim() && !labels.includes(editedLabelName.trim())) {
      setLabels(labels.map(l => l === editingLabel ? editedLabelName.trim() : l));
      setShowEditLabel(false);
      setEditingLabel('');
      setEditedLabelName('');
    }
  };

  const handleDeleteLabel = (label: string) => {
    setLabels(labels.filter(l => l !== label));
  };

  const handleAddColumn = () => {
    if (newColumnTitle.trim() && newColumnStatus.trim()) {
      const newColumn = {
        id: (columns.length + 1).toString(),
        title: newColumnTitle.toUpperCase(),
        status: newColumnStatus as any,
        issues: [],
        limit: newColumnLimit ? parseInt(newColumnLimit) : undefined
      };
      addColumn(newColumn);
      setShowAddColumn(false);
      setNewColumnTitle('');
      setNewColumnStatus('');
      setNewColumnLimit('');
    }
  };

  const availableUsers = mockUsers.filter(u => !teamMembers.find(m => m.id === u.id));

  return (
    <div className="h-screen bg-jira-gray-50 flex flex-col overflow-hidden">
      <JiraHeader />

      <div className="flex flex-1 overflow-hidden">
        <JiraSidebar />

        <main className="flex-1 overflow-y-auto">
          <div className="max-w-5xl mx-auto p-6">
            <div className="mb-6">
              <div className="flex items-center gap-3 mb-2">
                <Settings className="h-6 w-6 text-jira-gray-600" />
                <h1 className="text-2xl font-semibold text-jira-gray-900" data-testid="text-settings-title">
                  Project Settings
                </h1>
              </div>
              <p className="text-sm text-jira-gray-600">
                Configure your project settings, team members, and workflows
              </p>
            </div>

            <Tabs defaultValue="details" className="w-full">
              <TabsList className="mb-6" data-testid="tabs-settings">
                <TabsTrigger value="details" data-testid="tab-details">
                  <Settings className="h-4 w-4 mr-2" />
                  Details
                </TabsTrigger>
                <TabsTrigger value="team" data-testid="tab-team">
                  <Users className="h-4 w-4 mr-2" />
                  Team
                </TabsTrigger>
                <TabsTrigger value="labels" data-testid="tab-labels">
                  <Tag className="h-4 w-4 mr-2" />
                  Labels
                </TabsTrigger>
                <TabsTrigger value="workflow" data-testid="tab-workflow">
                  <Workflow className="h-4 w-4 mr-2" />
                  Workflow
                </TabsTrigger>
              </TabsList>

              <TabsContent value="details" className="space-y-6">
                <Card>
                  <CardHeader>
                    <CardTitle>Project Information</CardTitle>
                    <CardDescription>
                      Update your project's basic information
                    </CardDescription>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    <div className="space-y-2">
                      <Label htmlFor="project-name">Project Name *</Label>
                      <Input
                        id="project-name"
                        value={projectName}
                        onChange={(e) => setProjectName(e.target.value)}
                        data-testid="input-project-name"
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="project-key">Project Key *</Label>
                      <Input
                        id="project-key"
                        value={projectKey}
                        onChange={(e) => setProjectKey(e.target.value)}
                        maxLength={10}
                        data-testid="input-project-key"
                      />
                      <p className="text-xs text-jira-gray-500">
                        This is used as a prefix for issue keys (e.g., PROJ-123)
                      </p>
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="project-description">Description</Label>
                      <Textarea
                        id="project-description"
                        value={projectDescription}
                        onChange={(e) => setProjectDescription(e.target.value)}
                        rows={4}
                        data-testid="textarea-project-description"
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="project-category">Category</Label>
                      <Input
                        id="project-category"
                        value={project.category}
                        readOnly
                        data-testid="input-project-category"
                      />
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardHeader>
                    <CardTitle className="text-red-600">Danger Zone</CardTitle>
                    <CardDescription>
                      Irreversible actions for this project
                    </CardDescription>
                  </CardHeader>
                  <CardContent className="space-y-3">
                    <div className="flex items-center justify-between p-3 border border-jira-gray-200 rounded-md">
                      <div>
                        <p className="font-medium text-jira-gray-900">Archive Project</p>
                        <p className="text-sm text-jira-gray-600">Archive this project and all its issues</p>
                      </div>
                      <Button variant="outline" size="sm" data-testid="button-archive">
                        <Archive className="h-4 w-4 mr-2" />
                        Archive
                      </Button>
                    </div>

                    <div className="flex items-center justify-between p-3 border border-red-200 rounded-md">
                      <div>
                        <p className="font-medium text-red-600">Delete Project</p>
                        <p className="text-sm text-jira-gray-600">Permanently delete this project</p>
                      </div>
                      <Button 
                        variant="destructive" 
                        size="sm" 
                        data-testid="button-delete"
                        onClick={() => setShowDeleteWarning(true)}
                      >
                        <Trash2 className="h-4 w-4 mr-2" />
                        Delete
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              </TabsContent>

              <TabsContent value="team" className="space-y-6">
                <Card>
                  <CardHeader>
                    <div className="flex items-center justify-between">
                      <div>
                        <CardTitle>Team Members</CardTitle>
                        <CardDescription>
                          Manage who has access to this project
                        </CardDescription>
                      </div>
                      <Button onClick={() => setShowAddMember(true)} data-testid="button-add-member">
                        <Plus className="h-4 w-4 mr-2" />
                        Add Member
                      </Button>
                    </div>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-3">
                      {teamMembers.map((user, index) => (
                        <div key={user.id} className="flex items-center justify-between p-3 border border-jira-gray-200 rounded-md">
                          <div className="flex items-center gap-3">
                            <Avatar>
                              <AvatarImage src={user.avatarUrl} alt={user.name} />
                              <AvatarFallback className="bg-jira-blue text-white">
                                {user.initials}
                              </AvatarFallback>
                            </Avatar>
                            <div>
                              <p className="font-medium text-jira-gray-900">{user.name}</p>
                              <p className="text-sm text-jira-gray-600">{user.email}</p>
                            </div>
                          </div>
                          <div className="flex items-center gap-3">
                            {index === 0 && (
                              <Badge variant="secondary">Project Lead</Badge>
                            )}
                            {index !== 0 && (
                              <Button 
                                variant="ghost" 
                                size="sm" 
                                onClick={() => handleRemoveMember(user.id)}
                                data-testid={`button-remove-${user.id}`}
                              >
                                Remove
                              </Button>
                            )}
                          </div>
                        </div>
                      ))}
                    </div>
                  </CardContent>
                </Card>
              </TabsContent>

              <TabsContent value="labels" className="space-y-6">
                <Card>
                  <CardHeader>
                    <div className="flex items-center justify-between">
                      <div>
                        <CardTitle>Issue Labels</CardTitle>
                        <CardDescription>
                          Create and manage labels for categorizing issues
                        </CardDescription>
                      </div>
                      <Button onClick={() => setShowCreateLabel(true)} data-testid="button-create-label">
                        <Plus className="h-4 w-4 mr-2" />
                        Create Label
                      </Button>
                    </div>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-3">
                      {labels.map((label) => (
                        <div key={label} className="flex items-center justify-between p-3 border border-jira-gray-200 rounded-md">
                          <div className="flex items-center gap-3">
                            <Badge variant="secondary" className="text-sm">{label}</Badge>
                            <span className="text-sm text-jira-gray-600">Used in 3 issues</span>
                          </div>
                          <div className="flex gap-2">
                            <Button 
                              variant="ghost" 
                              size="sm" 
                              onClick={() => {
                                setEditingLabel(label);
                                setEditedLabelName(label);
                                setShowEditLabel(true);
                              }}
                              data-testid={`button-edit-${label}`}
                            >
                              <Edit className="h-4 w-4 mr-1" />
                              Edit
                            </Button>
                            <Button 
                              variant="ghost" 
                              size="sm" 
                              onClick={() => handleDeleteLabel(label)}
                              data-testid={`button-delete-${label}`}
                            >
                              <Trash2 className="h-4 w-4 mr-1 text-red-600" />
                              Delete
                            </Button>
                          </div>
                        </div>
                      ))}
                    </div>
                  </CardContent>
                </Card>
              </TabsContent>

              <TabsContent value="workflow" className="space-y-6">
                <Card>
                  <CardHeader>
                    <CardTitle>Workflow Configuration</CardTitle>
                    <CardDescription>
                      Configure the workflow stages for your project
                    </CardDescription>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-4">
                      {columns.map((column) => (
                        <div key={column.id} className="p-4 border border-jira-gray-200 rounded-md">
                          <div className="flex items-center justify-between mb-3">
                            <h4 className="font-medium text-jira-gray-900">Column: {column.title}</h4>
                            <Badge variant="secondary">Status: {column.status}</Badge>
                          </div>
                          <div className="flex items-center justify-between">
                            <span className="text-sm text-jira-gray-600">
                              WIP Limit: {column.limit || 'None'}
                            </span>
                            <Button variant="ghost" size="sm">Configure</Button>
                          </div>
                        </div>
                      ))}

                      <Button 
                        variant="outline" 
                        className="w-full" 
                        onClick={() => setShowAddColumn(true)}
                        data-testid="button-add-column"
                      >
                        <Plus className="h-4 w-4 mr-2" />
                        Add Column
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              </TabsContent>
            </Tabs>
          </div>
        </main>
      </div>

      {/* Add Member Dialog */}
      <Dialog open={showAddMember} onOpenChange={setShowAddMember}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Add Team Member</DialogTitle>
            <DialogDescription>
              Select a user to add to this project
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <Select value={selectedUser} onValueChange={setSelectedUser}>
              <SelectTrigger>
                <SelectValue placeholder="Select a user" />
              </SelectTrigger>
              <SelectContent>
                {availableUsers.map((user) => (
                  <SelectItem key={user.id} value={user.id}>
                    <div className="flex items-center gap-2">
                      <Avatar className="h-6 w-6">
                        <AvatarFallback className="bg-jira-blue text-white text-xs">
                          {user.initials}
                        </AvatarFallback>
                      </Avatar>
                      <span>{user.name}</span>
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setShowAddMember(false)}>Cancel</Button>
            <Button onClick={handleAddMember}>Add Member</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Create Label Dialog */}
      <Dialog open={showCreateLabel} onOpenChange={setShowCreateLabel}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Create Label</DialogTitle>
            <DialogDescription>
              Enter a name for the new label
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="new-label">Label Name</Label>
              <Input
                id="new-label"
                value={newLabelName}
                onChange={(e) => setNewLabelName(e.target.value)}
                placeholder="e.g., bug-fix, feature"
              />
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setShowCreateLabel(false)}>Cancel</Button>
            <Button onClick={handleCreateLabel}>Create</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Edit Label Dialog */}
      <Dialog open={showEditLabel} onOpenChange={setShowEditLabel}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Edit Label</DialogTitle>
            <DialogDescription>
              Update the label name
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="edit-label">Label Name</Label>
              <Input
                id="edit-label"
                value={editedLabelName}
                onChange={(e) => setEditedLabelName(e.target.value)}
              />
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setShowEditLabel(false)}>Cancel</Button>
            <Button onClick={handleEditLabel}>Save</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Add Column Dialog */}
      <Dialog open={showAddColumn} onOpenChange={setShowAddColumn}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Add Workflow Column</DialogTitle>
            <DialogDescription>
              Create a new column for your workflow
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="column-title">Column Title</Label>
              <Input
                id="column-title"
                value={newColumnTitle}
                onChange={(e) => setNewColumnTitle(e.target.value)}
                placeholder="e.g., In Review"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="column-status">Status Value</Label>
              <Input
                id="column-status"
                value={newColumnStatus}
                onChange={(e) => setNewColumnStatus(e.target.value)}
                placeholder="e.g., in-review"
              />
              <p className="text-xs text-jira-gray-500">
                Use lowercase with hyphens (e.g., in-review, ready-to-deploy)
              </p>
            </div>
            <div className="space-y-2">
              <Label htmlFor="column-limit">WIP Limit (optional)</Label>
              <Input
                id="column-limit"
                type="number"
                value={newColumnLimit}
                onChange={(e) => setNewColumnLimit(e.target.value)}
                placeholder="e.g., 5"
              />
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setShowAddColumn(false)}>Cancel</Button>
            <Button onClick={handleAddColumn}>Add Column</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Delete Warning Dialog */}
      <AlertDialog open={showDeleteWarning} onOpenChange={setShowDeleteWarning}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <div className="flex items-center gap-3">
              <div className="h-12 w-18 rounded-full bg-red-100 flex items-center justify-center">
                <AlertTriangle className="h-6 w-6 text-red-600" />
              </div>
              <div>
                <AlertDialogTitle>Please Don't Do That!</AlertDialogTitle>
                <AlertDialogDescription>
                  Deleting this project would remove all issues, comments, and history. This action cannot be undone. Are you absolutely sure you want to proceed?
                </AlertDialogDescription>
              </div>
            </div>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>I Changed My Mind</AlertDialogCancel>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
}

client/src/pages/Roadmap.tsx.content:
import { useState } from 'react';
import JiraHeader from '@/components/JiraHeader';
import JiraSidebar from '@/components/JiraSidebar';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Plus, Calendar, ChevronLeft, ChevronRight } from 'lucide-react';
import { Badge } from '@/components/ui/badge';
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { mockProjects, mockIssues } from '@/data/mockData';

interface Epic {
  id: string;
  key: string;
  summary: string;
  startDate: string;
  endDate: string;
  progress: number;
  status: string;
  issues: any[];
}

const quarters2024 = [
  { id: 'Q1-2024', label: 'Q1 2024', months: ['Jan', 'Feb', 'Mar'], year: 2024 },
  { id: 'Q2-2024', label: 'Q2 2024', months: ['Apr', 'May', 'Jun'], year: 2024 },
  { id: 'Q3-2024', label: 'Q3 2024', months: ['Jul', 'Aug', 'Sep'], year: 2024 },
  { id: 'Q4-2024', label: 'Q4 2024', months: ['Oct', 'Nov', 'Dec'], year: 2024 },
];

const quarters2025 = [
  { id: 'Q1-2025', label: 'Q1 2025', months: ['Jan', 'Feb', 'Mar'], year: 2025 },
  { id: 'Q2-2025', label: 'Q2 2025', months: ['Apr', 'May', 'Jun'], year: 2025 },
  { id: 'Q3-2025', label: 'Q3 2025', months: ['Jul', 'Aug', 'Sep'], year: 2025 },
  { id: 'Q4-2025', label: 'Q4 2025', months: ['Oct', 'Nov', 'Dec'], year: 2025 },
];

const quarters2026 = [
  { id: 'Q1-2026', label: 'Q1 2026', months: ['Jan', 'Feb', 'Mar'], year: 2026 },
  { id: 'Q2-2026', label: 'Q2 2026', months: ['Apr', 'May', 'Jun'], year: 2026 },
  { id: 'Q3-2026', label: 'Q3 2026', months: ['Jul', 'Aug', 'Sep'], year: 2026 },
  { id: 'Q4-2026', label: 'Q4 2026', months: ['Oct', 'Nov', 'Dec'], year: 2026 },
];

const initialEpics: Epic[] = [
  {
    id: '1',
    key: 'Q2-2024',
    summary: 'Completed Bachelor\'s in Information Technology',
    startDate: '2024-04-01',
    endDate: '2024-06-30',
    progress: 100,
    status: 'completed',
    issues: [],
  },
  {
    id: '2',
    key: 'Q3-2024',
    summary: 'Joined Hydro Online as Full Stack Developer',
    startDate: '2024-07-01',
    endDate: '2024-09-30',
    progress: 100,
    status: 'completed',
    issues: [],
  },
  {
    id: '3',
    key: 'Q4-2024',
    summary: 'OpenRTB JS Development',
    startDate: '2024-10-01',
    endDate: '2024-12-31',
    progress: 85,
    status: 'in-progress',
    issues: [],
  },
  {
    id: '4',
    key: 'Q1-2025',
    summary: 'Hydro Online Main Site & Payment Infrastructure',
    startDate: '2025-01-01',
    endDate: '2025-03-31',
    progress: 0,
    status: 'planned',
    issues: [],
  },
  {
    id: '5',
    key: 'Q2-2025',
    summary: 'Decentralized File Transfer System',
    startDate: '2025-04-01',
    endDate: '2025-06-30',
    progress: 0,
    status: 'planned',
    issues: [],
  },
  {
    id: '6',
    key: 'Q3-2025',
    summary: 'Active Interviewing & Career Opportunities',
    startDate: '2025-07-01',
    endDate: '2025-09-30',
    progress: 0,
    status: 'planned',
    issues: [],
  },
  {
    id: '7',
    key: 'Q1-2026',
    summary: 'Jr. Penetration Tester Certification',
    startDate: '2026-01-01',
    endDate: '2026-03-31',
    progress: 0,
    status: 'planned',
    issues: [],
  },
];

export default function Roadmap() {
  const currentProject = mockProjects[0];
  const [selectedYear, setSelectedYear] = useState(2025);
  const [epics, setEpics] = useState<Epic[]>(initialEpics);
  const [showCreateEpic, setShowCreateEpic] = useState(false);
  const [newEpic, setNewEpic] = useState({
    summary: '',
    description: '',
    startDate: '',
    endDate: ''
  });

  const getQuarters = (year: number) => {
    switch(year) {
      case 2024: return quarters2024;
      case 2025: return quarters2025;
      case 2026: return quarters2026;
      default: return quarters2024;
    }
  };

  const quarters = getQuarters(selectedYear);

  const getEpicPosition = (startDate: string, endDate: string) => {
    const start = new Date(startDate);
    const end = new Date(endDate);
    const yearStart = new Date(`${selectedYear}-01-01`);

    const startMonth = (start.getFullYear() - selectedYear) * 12 + start.getMonth();
    const endMonth = (end.getFullYear() - selectedYear) * 12 + end.getMonth();
    const duration = endMonth - startMonth + 1;

    return {
      gridColumn: `${startMonth + 1} / span ${Math.max(1, duration)}`,
    };
  };

  const handlePreviousYear = () => {
    if (selectedYear > 2024) {
      setSelectedYear(selectedYear - 1);
    }
  };

  const handleNextYear = () => {
    if (selectedYear < 2026) {
      setSelectedYear(selectedYear + 1);
    }
  };

  const handleCreateEpic = () => {
    const epic: Epic = {
      id: Date.now().toString(),
      key: `${currentProject.key}-${100 + epics.length}`,
      summary: newEpic.summary,
      startDate: newEpic.startDate,
      endDate: newEpic.endDate,
      progress: 0,
      status: 'planned',
      issues: []
    };

    setEpics([...epics, epic]);
    setShowCreateEpic(false);
    setNewEpic({ summary: '', description: '', startDate: '', endDate: '' });
  };

  const filteredEpics = epics.filter(epic => {
    const epicYear = new Date(epic.startDate).getFullYear();
    return epicYear === selectedYear;
  });

  return (
    <div className="h-screen bg-white flex flex-col overflow-hidden">
      <JiraHeader />

      <div className="flex flex-1 overflow-hidden">
        <JiraSidebar />

        <main className="flex-1 flex flex-col overflow-auto">
          <div className="border-b border-jira-gray-200 bg-white sticky top-0 z-10">
            <div className="px-6 py-4">
              <div className="flex items-center justify-between">
                <div>
                  <h1 className="text-2xl font-semibold text-jira-gray-900">Career Roadmap</h1>
                  <p className="text-sm text-jira-gray-500 mt-1">My career journey from graduation to current role at Hydro Online and future plans</p>
                </div>
                <div className="flex items-center gap-2">
                  <Button variant="outline" size="sm">
                    <Calendar className="h-4 w-4 mr-2" />
                    {selectedYear}
                  </Button>
                  <Button 
                    className="bg-jira-blue hover:bg-jira-blue-dark text-white"
                    onClick={() => setShowCreateEpic(true)}
                    data-testid="button-create-epic"
                  >
                    <Plus className="h-4 w-4 mr-2" />
                    Create epic
                  </Button>
                </div>
              </div>
            </div>
          </div>

          <div className="flex-1 px-6 py-6 overflow-auto">
            <div className="mb-6">
              <div className="flex items-center justify-between mb-4">
                <div className="flex items-center gap-4">
                  <Button 
                    variant="ghost" 
                    size="icon" 
                    className="h-8 w-8"
                    onClick={handlePreviousYear}
                    disabled={selectedYear <= 2024}
                  >
                    <ChevronLeft className="h-4 w-4" />
                  </Button>
                  <h2 className="text-lg font-medium text-jira-gray-900">{selectedYear}</h2>
                  <Button 
                    variant="ghost" 
                    size="icon" 
                    className="h-8 w-8"
                    onClick={handleNextYear}
                    disabled={selectedYear >= 2026}
                  >
                    <ChevronRight className="h-4 w-4" />
                  </Button>
                </div>
              </div>

              <div className="relative">
                <div className="grid grid-cols-12 gap-0 border-t border-l border-jira-gray-200">
                  {quarters.map(quarter => (
                    <div key={quarter.id} className="col-span-3 border-r border-jira-gray-200">
                      <div className="bg-jira-gray-50 border-b border-jira-gray-200 p-2 text-center">
                        <span className="text-sm font-medium text-jira-gray-700">{quarter.label}</span>
                      </div>
                      <div className="grid grid-cols-3">
                        {quarter.months.map(month => (
                          <div key={month} className="border-r border-b border-jira-gray-200 p-2 text-center last:border-r-0">
                            <span className="text-xs text-jira-gray-500">{month}</span>
                          </div>
                        ))}
                      </div>
                    </div>
                  ))}
                </div>

                <div className="mt-6 space-y-4">
                  {filteredEpics.map(epic => (
                    <div key={epic.id} className="relative">
                      <div className="grid grid-cols-12 gap-0 relative h-20">
                        <div 
                          className="absolute h-16 bg-jira-blue rounded-lg p-3 cursor-pointer hover:bg-jira-blue-dark transition-colors z-10"
                          style={getEpicPosition(epic.startDate, epic.endDate)}
                        >
                          <div className="flex items-center justify-between mb-1">
                            <div className="flex items-center gap-2">
                              <span className="text-xs font-medium text-white">{epic.key}</span>
                              <Badge variant="outline" className="bg-white/20 text-white border-white/40 text-xs">
                                {epic.issues.length} issues
                              </Badge>
                            </div>
                            <span className="text-xs text-white">{epic.progress}%</span>
                          </div>
                          <p className="text-sm font-medium text-white truncate">{epic.summary}</p>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>

            <div className="mt-8 border-t border-jira-gray-200 pt-6">
              <h3 className="text-lg font-medium text-jira-gray-900 mb-4">Timeline Legend</h3>
              <div className="flex items-center gap-6">
                <div className="flex items-center gap-2">
                  <div className="h-4 w-12 bg-jira-blue rounded"></div>
                  <span className="text-sm text-jira-gray-700">In Progress</span>
                </div>
                <div className="flex items-center gap-2">
                  <div className="h-4 w-12 bg-jira-gray-300 rounded"></div>
                  <span className="text-sm text-jira-gray-700">Planned</span>
                </div>
                <div className="flex items-center gap-2">
                  <div className="h-4 w-12 bg-green-500 rounded"></div>
                  <span className="text-sm text-jira-gray-700">Completed</span>
                </div>
              </div>
            </div>
          </div>
        </main>
      </div>

      {/* Create Epic Dialog */}
      <Dialog open={showCreateEpic} onOpenChange={setShowCreateEpic}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Create Epic</DialogTitle>
            <DialogDescription>
              Create a new epic for your roadmap
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="epic-summary">Epic Summary *</Label>
              <Input
                id="epic-summary"
                placeholder="e.g., Payment Integration"
                value={newEpic.summary}
                onChange={(e) => setNewEpic({ ...newEpic, summary: e.target.value })}
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="epic-description">Description</Label>
              <Textarea
                id="epic-description"
                placeholder="Describe the epic..."
                rows={3}
                value={newEpic.description}
                onChange={(e) => setNewEpic({ ...newEpic, description: e.target.value })}
              />
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="start-date">Start Date *</Label>
                <Input
                  id="start-date"
                  type="date"
                  value={newEpic.startDate}
                  onChange={(e) => setNewEpic({ ...newEpic, startDate: e.target.value })}
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="end-date">End Date *</Label>
                <Input
                  id="end-date"
                  type="date"
                  value={newEpic.endDate}
                  onChange={(e) => setNewEpic({ ...newEpic, endDate: e.target.value })}
                />
              </div>
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setShowCreateEpic(false)}>Cancel</Button>
            <Button onClick={handleCreateEpic}>Create Epic</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

